package com.example.foodnote.ui.recipes_fragment

import android.os.Bundle
import android.view.View
import android.widget.Toast
import com.bumptech.glide.Glide
import com.example.foodnote.databinding.FragmentRecipesBinding
import com.example.foodnote.ui.base.BaseViewBindingFragment
import com.google.android.material.bottomsheet.BottomSheetBehavior


class RecipesFragment : BaseViewBindingFragment<FragmentRecipesBinding>(FragmentRecipesBinding::inflate) {

    private lateinit var adapterRecipes:RecipesAdapter
    private var behavior: BottomSheetBehavior<*>? = null
    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        behavior = BottomSheetBehavior.from(binding.includeRecipesDetails.bottomSheet)
        behavior!!.addBottomSheetCallback(object : BottomSheetBehavior.BottomSheetCallback() {
            override fun onStateChanged(bottomSheet: View, newState: Int) {}
            override fun onSlide(bottomSheet: View, slideOffset: Float) {}
        })
        adapterRecipes = RecipesAdapter(Glide.with(requireContext()),object :RecipesListener{
            override fun onClickRecipes(recipes: Recipes) {
                behavior!!.state = BottomSheetBehavior.STATE_EXPANDED
                Glide.with(requireContext()).load(recipes.imageUrl).into(binding.includeRecipesDetails.imageViewRecipes)
                binding.includeRecipesDetails.textViewNameRecipes.text = recipes.nameRecipes
                binding.includeRecipesDetails.textViewDescriptionRecipes.text = recipes.descriptionRecipes
            }

            override fun onClickRecipesLike(recipes: Recipes) {
                Toast.makeText(requireContext(),"В избранное добавлен рецепт ${recipes.nameRecipes}",Toast.LENGTH_LONG).show()
            }
        })

        binding.recyclerViewRecipes.adapter = adapterRecipes
val url = "data:image/jpeg;base64,"
val url2 = "data:image/jpeg;base64,"

        val list = listOf<Recipes>(Recipes(url,"Оливье","fsdfsdfsdfsdfsdf"),
            Recipes(url,"Картошка фри","fsdfsdfsdfsdfsdf"),
            Recipes(url2,"Шашлык","fsdfsdfsdfsdfsdf"),
            Recipes(url2,"Печеный угорь","fsdfsdfsdfsdfsdf"),
            Recipes(url,"Индейка","fsdfsdfsdfsdfsdf"),
            Recipes(url,"Курица в кляре","fsdfsdfsdfsdfsdf"),
            Recipes(url2,"Яишница","fsdfsdfsdfsdfsdf"))

        adapterRecipes.submitList(list)

    }
}